import React, { useEffect, useRef } from 'react';
import { MapPin } from 'lucide-react';
import { Doctor } from '../types';

interface LocationMapProps {
  doctors: Doctor[];
  userLocation?: { lat: number; lng: number } | null;
}

const LocationMap: React.FC<LocationMapProps> = ({ doctors, userLocation }) => {
  const mapRef = useRef<HTMLDivElement>(null);
  const mapInstanceRef = useRef<any>(null);

  useEffect(() => {
    if (!mapRef.current || doctors.length === 0) return;

    // Load Google Maps script
    const loadGoogleMaps = () => {
      if (window.google && window.google.maps) {
        initMap();
        return;
      }

      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyBFw0Qbyq9zTFTd-tUY6dZWTgaQzuU17R8`;
      script.async = true;
      script.defer = true;
      script.onload = initMap;
      document.head.appendChild(script);
    };

    const initMap = () => {
      if (!mapRef.current || !window.google) return;

      // Calculate center based on doctors or user location
      const center = userLocation
        ? { lat: userLocation.lat, lng: userLocation.lng }
        : doctors[0]?.location.coordinates?.coordinates
        ? { lat: doctors[0].location.coordinates.coordinates[1], lng: doctors[0].location.coordinates.coordinates[0] }
        : { lat: 28.6139, lng: 77.2090 }; // Default to Delhi

      const map = new window.google.maps.Map(mapRef.current, {
        center,
        zoom: 12,
        styles: [
          {
            featureType: 'poi',
            elementType: 'labels',
            stylers: [{ visibility: 'off' }]
          }
        ]
      });

      mapInstanceRef.current = map;

      // Add user location marker
      if (userLocation) {
        new window.google.maps.Marker({
          position: { lat: userLocation.lat, lng: userLocation.lng },
          map,
          icon: {
            path: window.google.maps.SymbolPath.CIRCLE,
            scale: 10,
            fillColor: '#3B82F6',
            fillOpacity: 1,
            strokeColor: '#FFFFFF',
            strokeWeight: 3
          },
          title: 'Your Location'
        });
      }

      // Add doctor markers
      doctors.forEach((doctor, index) => {
        if (doctor.location.coordinates?.coordinates) {
          const position = {
            lat: doctor.location.coordinates.coordinates[1],
            lng: doctor.location.coordinates.coordinates[0]
          };

          const marker = new window.google.maps.Marker({
            position,
            map,
            icon: {
              path: window.google.maps.SymbolPath.CIRCLE,
              scale: 8,
              fillColor: '#EF4444',
              fillOpacity: 1,
              strokeColor: '#FFFFFF',
              strokeWeight: 2
            },
            title: `Dr. ${doctor.name}`
          });

          const infoWindow = new window.google.maps.InfoWindow({
            content: `
              <div style="padding: 8px; max-width: 200px;">
                <h3 style="font-weight: bold; margin-bottom: 4px;">Dr. ${doctor.name}</h3>
                <p style="font-size: 12px; color: #6B7280; margin-bottom: 4px;">${doctor.specialty}</p>
                <p style="font-size: 11px; color: #9CA3AF;">${doctor.location.address}</p>
                <p style="font-size: 11px; color: #9CA3AF;">${doctor.location.city}</p>
              </div>
            `
          });

          marker.addListener('click', () => {
            infoWindow.open(map, marker);
          });
        }
      });
    };

    loadGoogleMaps();
  }, [doctors, userLocation]);

  return (
    <div className="h-80 w-full rounded-lg overflow-hidden" ref={mapRef}>
      {/* Map will be rendered here */}
    </div>
  );
};

export default LocationMap;
