import React from 'react';
import { Star, MapPin, Clock, DollarSign } from 'lucide-react';
import { Doctor } from '../types';

// Enhanced animation styles
const animationStyles = `
  @keyframes slide-up {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .animate-slide-up {
    animation: slide-up 0.6s ease-out;
  }
`;

// Inject enhanced styles
if (typeof document !== 'undefined') {
  const styleSheet = document.createElement('style');
  styleSheet.textContent = animationStyles;
  document.head.appendChild(styleSheet);
}

// Add staggered animation delay
const getAnimationDelay = (index: number) => {
  return { animationDelay: `${index * 100}ms` };
};

interface DoctorCardProps {
  doctor: Doctor;
  onBookAppointment: (doctorId: string) => void;
  onViewProfile: (doctorId: string) => void;
  index?: number;
  userLocation?: { lat: number; lng: number } | null;
  userCity?: string;
}

const DoctorCard: React.FC<DoctorCardProps> = ({ doctor, onBookAppointment, onViewProfile, index = 0, userLocation, userCity }) => {

  const calculateDistance = (lat1: number, lng1: number, lat2: number, lng2: number) => {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLng = (lng2 - lng1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
              Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
              Math.sin(dLng/2) * Math.sin(dLng/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  const getDistance = () => {
    if (userLocation && doctor.location.coordinates?.coordinates) {
      const distance = calculateDistance(
        userLocation.lat,
        userLocation.lng,
        doctor.location.coordinates.coordinates[1],
        doctor.location.coordinates.coordinates[0]
      );
      return distance.toFixed(1);
    }
    return null;
  };

  const distance = getDistance();
  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        className={`h-4 w-4 ${
          i < Math.floor(rating)
            ? 'text-yellow-400 fill-current'
            : 'text-gray-300'
        }`}
      />
    ));
  };

  return (
    <div className="group relative bg-white rounded-2xl shadow-md hover:shadow-lg border border-slate-100 overflow-hidden transform transition-all duration-300 hover:-translate-y-1 animate-slide-up" style={getAnimationDelay(index)}>
      {/* Gradient Background */}
      <div className="absolute inset-0 bg-gradient-to-br from-indigo-50/50 via-white to-purple-50/50 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>

      {/* Top Accent Bar */}
      <div className="h-0.5 bg-gradient-to-r from-indigo-500 via-purple-500 to-cyan-500"></div>

      <div className="relative p-4">
        {/* Header Section */}
        <div className="flex items-start justify-between mb-3">
          <div className="flex items-center space-x-3">
            {/* Doctor Avatar */}
            <div className="relative">
              <div className="w-14 h-14 gradient-primary rounded-2xl flex items-center justify-center text-white font-bold text-base shadow-md group-hover:scale-105 transition-transform duration-300">
                {doctor.name.split(' ').map(n => n[0]).join('')}
              </div>
              <div className="absolute -bottom-1 -right-1 w-5 h-5 bg-emerald-500 rounded-full border-2 border-white flex items-center justify-center shadow-md">
                <span className="text-white text-xs font-bold">‚úì</span>
              </div>
            </div>

            {/* Doctor Info */}
            <div className="flex-1">
              <h3 className="text-base font-bold text-slate-900 font-display group-hover:gradient-text transition-all duration-300 mb-1">
                Dr. {doctor.name}
              </h3>
              <div className="flex items-center space-x-2 mb-1">
                <span className="bg-gradient-to-r from-indigo-100 to-purple-100 text-indigo-700 px-2 py-0.5 rounded-full text-xs font-semibold border border-indigo-200">
                  {doctor.specialty}
                </span>
                <span className="bg-slate-100 text-slate-600 px-2 py-0.5 rounded-lg text-xs font-medium">
                  {doctor.experience}y
                </span>
              </div>
            </div>
          </div>

          {/* Rating Badge */}
          <div className="bg-gradient-to-r from-yellow-50 to-orange-50 rounded-xl p-2 border border-yellow-200 text-center min-w-[60px]">
            <div className="flex items-center justify-center space-x-0.5 mb-0.5">
              <Star className="h-3 w-3 text-yellow-400 fill-current" />
            </div>
            <div className="text-base font-bold text-slate-900">{doctor.rating}</div>
            <div className="text-[10px] text-slate-500">({doctor.reviewCount})</div>
          </div>
        </div>

        {/* Info Cards Grid */}
        <div className="grid grid-cols-3 gap-2 mb-3">
          <div className="bg-gradient-to-br from-blue-50 to-indigo-50 rounded-xl p-2 border border-blue-100 group-hover:from-blue-100 group-hover:to-indigo-100 transition-all duration-300">
            <div className="flex items-center space-x-1 mb-0.5">
              <MapPin className="h-3 w-3 text-indigo-500" />
              <span className="text-[10px] font-medium text-indigo-600">Location</span>
            </div>
            <div className="text-xs font-semibold text-slate-800 truncate">{doctor.location.city}</div>
            {distance && (
              <div className="text-[10px] text-indigo-600 font-medium mt-0.5">{distance} km</div>
            )}
          </div>

          <div className="bg-gradient-to-br from-emerald-50 to-green-50 rounded-xl p-2 border border-emerald-100 group-hover:from-emerald-100 group-hover:to-green-100 transition-all duration-300">
            <div className="flex items-center space-x-1 mb-0.5">
              <DollarSign className="h-3 w-3 text-emerald-500" />
              <span className="text-[10px] font-medium text-emerald-600">Fee</span>
            </div>
            <div className="text-xs font-bold text-slate-800">‚Çπ{doctor.consultationFee}</div>
          </div>

          <div className="bg-gradient-to-br from-purple-50 to-pink-50 rounded-xl p-2 border border-purple-100 group-hover:from-purple-100 group-hover:to-pink-100 transition-all duration-300">
            <div className="flex items-center space-x-1 mb-0.5">
              <Clock className="h-3 w-3 text-purple-500" />
              <span className="text-[10px] font-medium text-purple-600">Available</span>
            </div>
            <div className="text-xs font-semibold text-slate-800">Today</div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex space-x-2">
          <button
            onClick={() => onBookAppointment(doctor._id)}
            className="flex-1 gradient-primary text-white py-2 px-3 rounded-xl font-semibold text-xs shadow-md hover:shadow-lg transform hover:scale-105 transition-all duration-300 flex items-center justify-center space-x-1"
          >
            <span>üìÖ</span>
            <span>Book</span>
          </button>
          <button 
            onClick={() => onViewProfile(doctor._id)}
            className="bg-gradient-to-r from-slate-100 to-slate-200 text-slate-700 py-2 px-3 rounded-xl font-medium text-xs hover:from-slate-200 hover:to-slate-300 transform hover:scale-105 transition-all duration-300 flex items-center justify-center space-x-1 border border-slate-200"
          >
            <span>üëÅÔ∏è</span>
            <span>Profile</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default DoctorCard;
