import React, { useState, useRef, useEffect } from 'react';
import { MessageCircle, X, Send, Bot } from 'lucide-react';

interface Message {
  text: string;
  isBot: boolean;
  timestamp: Date;
}

const Chatbot: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState<Message[]>([
    { text: "Hi! I'm SehatNama Assistant. ðŸ‘‹\n\nI can help you with:\nâ€¢ Finding doctors\nâ€¢ Booking appointments\nâ€¢ Pricing information\nâ€¢ Location services\n\nWhat would you like to know?", isBot: true, timestamp: new Date() }
  ]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const getBotResponse = (userMessage: string): string => {
    const msg = userMessage.toLowerCase();

    // Greetings
    if (msg.match(/\b(hello|hi|hey|good morning|good evening|good afternoon)\b/)) {
      return "Hello! Welcome to SehatNama. I can help you find doctors, book appointments, or answer questions about our services. What would you like to know?";
    }

    // Booking appointments
    if (msg.match(/\b(book|schedule|make)\b.*\b(appointment|consultation)\b/)) {
      return "To book an appointment:\n1. Search for a doctor by specialty or location\n2. Click 'Book Appointment' on their profile\n3. Select date and time slot\n4. Confirm booking\n\nNote: You'll need to sign in first.";
    }

    // Finding doctors
    if (msg.match(/\b(find|search|looking for|need)\b.*\b(doctor|specialist|physician)\b/)) {
      return "You can find doctors by:\nâ€¢ Using the search bar at the top\nâ€¢ Filtering by specialty (Cardiology, Dermatology, etc.)\nâ€¢ Searching by location or city\nâ€¢ Sorting by rating, distance, or price\n\nWe have 500+ verified doctors across India!";
    }

    // Canceling appointments
    if (msg.match(/\b(cancel|delete|remove)\b.*\b(appointment|booking)\b/)) {
      return "To cancel an appointment:\n1. Go to 'My Appointments' section\n2. Find your scheduled appointment\n3. Click the 'Cancel' button\n\nPlease cancel at least 24 hours in advance when possible.";
    }

    // Pricing
    if (msg.match(/\b(price|fee|cost|charge|how much)\b/)) {
      return "Consultation fees vary by doctor and specialty:\nâ€¢ General Physician: â‚¹300-â‚¹500\nâ€¢ Specialists: â‚¹500-â‚¹1000\nâ€¢ Super Specialists: â‚¹1000-â‚¹1500\n\nYou can see each doctor's exact fee on their profile card.";
    }

    // Location services
    if (msg.match(/\b(location|near me|nearby|closest|distance)\b/)) {
      return "To find doctors near you:\n1. Click 'Use my location' in the search bar\n2. Allow location access\n3. We'll show doctors sorted by distance\n\nYou can also manually enter your city or area.";
    }

    // Specialties
    if (msg.match(/\b(specialty|specialties|type of doctor|which doctor)\b/)) {
      return "We have doctors in 50+ specialties including:\nâ€¢ Cardiology (Heart)\nâ€¢ Dermatology (Skin)\nâ€¢ Neurology (Brain & Nerves)\nâ€¢ Orthopedics (Bones & Joints)\nâ€¢ Pediatrics (Children)\nâ€¢ Gynecology (Women's Health)\nâ€¢ Psychiatry (Mental Health)\nâ€¢ General Medicine\n\nAnd many more!";
    }

    // Emergency
    if (msg.match(/\b(emergency|urgent|critical|serious|ambulance)\b/)) {
      return "âš ï¸ For medical emergencies:\nâ€¢ Call 108 (India Emergency Services)\nâ€¢ Visit the nearest hospital immediately\n\nOur platform is for scheduled consultations, not emergency care. Please seek immediate help for urgent medical situations.";
    }

    // Payment
    if (msg.match(/\b(payment|pay|transaction|upi|card)\b/)) {
      return "We accept multiple payment methods:\nâ€¢ UPI (Google Pay, PhonePe, Paytm)\nâ€¢ Credit/Debit Cards\nâ€¢ Net Banking\nâ€¢ Wallets\n\nAll payments are secure and encrypted. You'll receive instant confirmation.";
    }

    // Availability/Hours
    if (msg.match(/\b(hours|time|when|available|open)\b/)) {
      return "Our platform is available 24/7 for browsing and booking.\n\nDoctor availability varies:\nâ€¢ Check each doctor's profile for their consultation hours\nâ€¢ Most doctors are available 9 AM - 9 PM\nâ€¢ Some offer evening and weekend slots\n\nBook anytime, consult as per doctor's schedule!";
    }

    // Contact/Support
    if (msg.match(/\b(contact|support|help|call|email|reach)\b/)) {
      return "Contact our support team:\n\nðŸ“ž Phone: +91 1800-123-4567\nðŸ“§ Email: support@sehetnama.com\nðŸ“ Address: Mumbai, Maharashtra\nâ° Available: 24/7\n\nWe're always here to help!";
    }

    // Registration/Sign up
    if (msg.match(/\b(register|sign up|create account|join)\b/)) {
      return "To create an account:\n1. Click 'Sign In' at the top\n2. Select 'Register'\n3. Enter your details (name, email, phone)\n4. Verify your phone number\n5. Complete your profile\n\nIt only takes 2 minutes!";
    }

    // Reviews/Ratings
    if (msg.match(/\b(review|rating|feedback|testimonial)\b/)) {
      return "All our doctors have verified patient reviews and ratings.\n\nYou can:\nâ€¢ View ratings (1-5 stars)\nâ€¢ Read patient reviews\nâ€¢ See review count\nâ€¢ Filter by top-rated doctors\n\nAfter your consultation, you can also leave a review!";
    }

    // Thank you
    if (msg.match(/\b(thank|thanks|appreciate)\b/)) {
      return "You're very welcome! ðŸ˜Š\n\nFeel free to ask if you need anything else. Stay healthy and take care!";
    }

    // Goodbye
    if (msg.match(/\b(bye|goodbye|see you|later)\b/)) {
      return "Goodbye! Take care and stay healthy! ðŸ‘‹\n\nFeel free to come back anytime you need help. Have a great day!";
    }

    // Default response
    return "I can help you with:\n\nðŸ” Finding doctors\nðŸ“… Booking appointments\nâŒ Canceling appointments\nðŸ’° Payment & pricing info\nðŸ“ Location services\nðŸ¥ Specialties information\nðŸ“ž Contact support\n\nPlease ask me anything specific, and I'll provide detailed information!";
  };

  const handleSend = () => {
    if (!input.trim()) return;

    const userMessage: Message = {
      text: input,
      isBot: false,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');

    setTimeout(() => {
      const botMessage: Message = {
        text: getBotResponse(input),
        isBot: true,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, botMessage]);
    }, 500);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <>
      {/* Chat Button */}
      {!isOpen && (
        <button
          onClick={() => setIsOpen(true)}
          className="fixed bottom-6 right-6 w-16 h-16 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-full shadow-2xl hover:shadow-3xl transform hover:scale-110 transition-all duration-300 flex items-center justify-center z-50 animate-bounce"
        >
          <MessageCircle className="h-7 w-7" />
        </button>
      )}

      {/* Chat Window */}
      {isOpen && (
        <div className="fixed bottom-6 right-6 w-96 h-[600px] bg-white rounded-2xl shadow-2xl flex flex-col z-50 border border-gray-200 overflow-hidden">
          {/* Header */}
          <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-4 flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-white bg-opacity-20 rounded-full flex items-center justify-center">
                <Bot className="h-6 w-6" />
              </div>
              <div>
                <h3 className="font-bold text-lg">SehatNama Assistant</h3>
                <p className="text-xs text-blue-100">Online â€¢ Always here to help</p>
              </div>
            </div>
            <button
              onClick={() => setIsOpen(false)}
              className="hover:bg-white hover:bg-opacity-20 rounded-full p-2 transition-all"
            >
              <X className="h-5 w-5" />
            </button>
          </div>

          {/* Messages */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gray-50">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`flex ${message.isBot ? 'justify-start' : 'justify-end'}`}
              >
                <div
                  className={`max-w-[80%] rounded-2xl px-4 py-3 ${
                    message.isBot
                      ? 'bg-white text-gray-800 shadow-md border border-gray-200'
                      : 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg'
                  }`}
                >
                  {message.isBot && (
                    <div className="flex items-center space-x-2 mb-1">
                      <Bot className="h-4 w-4 text-blue-600" />
                      <span className="text-xs font-semibold text-blue-600">Assistant</span>
                    </div>
                  )}
                  <p className="text-sm whitespace-pre-line leading-relaxed">{message.text}</p>
                  <p className={`text-xs mt-1 ${message.isBot ? 'text-gray-400' : 'text-blue-100'}`}>
                    {message.timestamp.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' })}
                  </p>
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          {/* Input */}
          <div className="p-4 bg-white border-t border-gray-200">
            <div className="flex items-center space-x-2">
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Type your message..."
                className="flex-1 px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent text-sm"
              />
              <button
                onClick={handleSend}
                disabled={!input.trim()}
                className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-3 rounded-xl hover:shadow-lg transform hover:scale-105 transition-all disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <Send className="h-5 w-5" />
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Chatbot;
