import React, { useState, useRef, useEffect } from 'react';
import { MessageCircle, X, Send, Bot, Calendar } from 'lucide-react';
import apiService from '../services/api';
import { Doctor } from '../types';

interface Message {
  text: string;
  isBot: boolean;
  timestamp: Date;
  options?: string[];
  doctors?: Doctor[];
  showBooking?: boolean;
}

const Chatbot: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState<Message[]>([
    { 
      text: "Hi! I'm SehatNama Assistant. üëã\n\nHow can I help you today?", 
      isBot: true, 
      timestamp: new Date(),
      options: [
        "üîç Find Doctors",
        "üìÖ Book Appointment",
        "üí∞ Pricing Info",
        "üìç Location Services",
        "‚ùå Cancel Appointment",
        "üìû Contact Support"
      ]
    }
  ]);
  const [input, setInput] = useState('');
  const [bookingStep, setBookingStep] = useState<'specialty' | 'doctors' | null>(null);
  const [selectedSpecialty, setSelectedSpecialty] = useState<string>('');
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const getBotResponse = (userMessage: string): { text: string; options?: string[] } => {
    const msg = userMessage.toLowerCase();

    // Greetings
    if (msg.match(/\b(hello|hi|hey|good morning|good evening|good afternoon)\b/)) {
      return {
        text: "Hello! Welcome to SehatNama. I can help you find doctors, book appointments, or answer questions about our services.",
        options: [
          "üîç Find Doctors",
          "üìÖ Book Appointment",
          "üí∞ Pricing Info",
          "üìç Location Services",
          "üìû Contact Support"
        ]
      };
    }

    // Booking appointments
    if (msg.match(/\b(book|schedule|make|üìÖ)\b.*\b(appointment|consultation)\b/) || msg.includes('üìÖ')) {
      return {
        text: "Great! Let's book an appointment for you.\n\nPlease select a specialty:",
        options: [
          "‚ù§Ô∏è Cardiology",
          "üß† Neurology",
          "ü¶¥ Orthopedics",
          "üë∂ Pediatrics",
          "ü©∫ General Medicine",
          "üë© Gynecology",
          "üè† Main Menu"
        ],
        showBooking: true
      };
    }

    // Finding doctors
    if (msg.match(/\b(find|search|looking for|need|üîç)\b.*\b(doctor|specialist|physician)\b/) || msg.includes('üîç')) {
      return {
        text: "You can find doctors by:\n‚Ä¢ Using the search bar at the top\n‚Ä¢ Filtering by specialty (Cardiology, Dermatology, etc.)\n‚Ä¢ Searching by location or city\n‚Ä¢ Sorting by rating, distance, or price\n\nWe have 500+ verified doctors across India!",
        options: [
          "üè• View Specialties",
          "üìç Find Near Me",
          "üí∞ Check Pricing",
          "üìÖ Book Appointment",
          "üè† Main Menu"
        ]
      };
    }

    // Canceling appointments
    if (msg.match(/\b(cancel|delete|remove|‚ùå)\b.*\b(appointment|booking)\b/) || msg.includes('‚ùå')) {
      return {
        text: "To cancel an appointment:\n1. Go to 'My Appointments' section\n2. Find your scheduled appointment\n3. Click the 'Cancel' button\n\nPlease cancel at least 24 hours in advance when possible.",
        options: [
          "üìÖ Book New Appointment",
          "üìû Contact Support",
          "üè† Main Menu"
        ]
      };
    }

    // Pricing
    if (msg.match(/\b(price|fee|cost|charge|how much|üí∞)\b/) || msg.includes('üí∞')) {
      return {
        text: "Consultation fees vary by doctor and specialty:\n‚Ä¢ General Physician: ‚Çπ300-‚Çπ500\n‚Ä¢ Specialists: ‚Çπ500-‚Çπ1000\n‚Ä¢ Super Specialists: ‚Çπ1000-‚Çπ1500\n\nYou can see each doctor's exact fee on their profile card.",
        options: [
          "üîç Find Doctors",
          "üí≥ Payment Methods",
          "üìÖ Book Appointment",
          "üè† Main Menu"
        ]
      };
    }

    // Location services
    if (msg.match(/\b(location|near me|nearby|closest|distance|üìç)\b/) || msg.includes('üìç')) {
      return {
        text: "To find doctors near you:\n1. Click 'Use my location' in the search bar\n2. Allow location access\n3. We'll show doctors sorted by distance\n\nYou can also manually enter your city or area.",
        options: [
          "üîç Find Doctors",
          "üè• View Specialties",
          "üìÖ Book Appointment",
          "üè† Main Menu"
        ]
      };
    }

    // Specialties
    if (msg.match(/\b(specialty|specialties|type of doctor|which doctor|üè•)\b/) || msg.includes('üè•')) {
      return {
        text: "We have doctors in 50+ specialties including:\n‚Ä¢ Cardiology (Heart)\n‚Ä¢ Dermatology (Skin)\n‚Ä¢ Neurology (Brain & Nerves)\n‚Ä¢ Orthopedics (Bones & Joints)\n‚Ä¢ Pediatrics (Children)\n‚Ä¢ Gynecology (Women's Health)\n‚Ä¢ Psychiatry (Mental Health)\n‚Ä¢ General Medicine\n\nAnd many more!",
        options: [
          "üîç Find Doctors",
          "üìç Find Near Me",
          "üí∞ Check Pricing",
          "üìÖ Book Appointment",
          "üè† Main Menu"
        ]
      };
    }

    // Emergency
    if (msg.match(/\b(emergency|urgent|critical|serious|ambulance)\b/)) {
      return {
        text: "‚ö†Ô∏è For medical emergencies:\n‚Ä¢ Call 108 (India Emergency Services)\n‚Ä¢ Visit the nearest hospital immediately\n\nOur platform is for scheduled consultations, not emergency care. Please seek immediate help for urgent medical situations.",
        options: [
          "üìû Contact Support",
          "üè† Main Menu"
        ]
      };
    }

    // Payment
    if (msg.match(/\b(payment|pay|transaction|upi|card|üí≥)\b/) || msg.includes('üí≥')) {
      return {
        text: "We accept multiple payment methods:\n‚Ä¢ UPI (Google Pay, PhonePe, Paytm)\n‚Ä¢ Credit/Debit Cards\n‚Ä¢ Net Banking\n‚Ä¢ Wallets\n\nAll payments are secure and encrypted. You'll receive instant confirmation.",
        options: [
          "üí∞ Check Pricing",
          "üìÖ Book Appointment",
          "üìû Contact Support",
          "üè† Main Menu"
        ]
      };
    }

    // Availability/Hours
    if (msg.match(/\b(hours|time|when|available|open)\b/)) {
      return {
        text: "Our platform is available 24/7 for browsing and booking.\n\nDoctor availability varies:\n‚Ä¢ Check each doctor's profile for their consultation hours\n‚Ä¢ Most doctors are available 9 AM - 9 PM\n‚Ä¢ Some offer evening and weekend slots\n\nBook anytime, consult as per doctor's schedule!",
        options: [
          "üîç Find Doctors",
          "üìÖ Book Appointment",
          "üè† Main Menu"
        ]
      };
    }

    // Contact/Support
    if (msg.match(/\b(contact|support|help|call|email|reach|üìû)\b/) || msg.includes('üìû')) {
      return {
        text: "Contact our support team:\n\nüìû Phone: +91 1800-123-4567\nüìß Email: support@sehetnama.com\nüìç Address: Mumbai, Maharashtra\n‚è∞ Available: 24/7\n\nWe're always here to help!",
        options: [
          "üîç Find Doctors",
          "üìÖ Book Appointment",
          "üí∞ Pricing Info",
          "üè† Main Menu"
        ]
      };
    }

    // Registration/Sign up
    if (msg.match(/\b(register|sign up|create account|join)\b/)) {
      return {
        text: "To create an account:\n1. Click 'Sign In' at the top\n2. Select 'Register'\n3. Enter your details (name, email, phone)\n4. Verify your phone number\n5. Complete your profile\n\nIt only takes 2 minutes!",
        options: [
          "üìÖ Book Appointment",
          "üîç Find Doctors",
          "üìû Need Help?",
          "üè† Main Menu"
        ]
      };
    }

    // Reviews/Ratings
    if (msg.match(/\b(review|rating|feedback|testimonial)\b/)) {
      return {
        text: "All our doctors have verified patient reviews and ratings.\n\nYou can:\n‚Ä¢ View ratings (1-5 stars)\n‚Ä¢ Read patient reviews\n‚Ä¢ See review count\n‚Ä¢ Filter by top-rated doctors\n\nAfter your consultation, you can also leave a review!",
        options: [
          "üîç Find Top Rated Doctors",
          "üìÖ Book Appointment",
          "üè† Main Menu"
        ]
      };
    }

    // Thank you
    if (msg.match(/\b(thank|thanks|appreciate)\b/)) {
      return {
        text: "You're very welcome! üòä\n\nFeel free to ask if you need anything else. Stay healthy and take care!",
        options: [
          "üîç Find Doctors",
          "üìÖ Book Appointment",
          "üìû Contact Support",
          "üè† Main Menu"
        ]
      };
    }

    // Goodbye
    if (msg.match(/\b(bye|goodbye|see you|later)\b/)) {
      return {
        text: "Goodbye! Take care and stay healthy! üëã\n\nFeel free to come back anytime you need help. Have a great day!"
      };
    }

    // Default response
    return {
      text: "I can help you with:",
      options: [
        "üîç Find Doctors",
        "üìÖ Book Appointment",
        "üí∞ Pricing Info",
        "üìç Location Services",
        "‚ùå Cancel Appointment",
        "üìû Contact Support"
      ]
    };
  };

  const handleSend = () => {
    if (!input.trim()) return;

    const userMessage: Message = {
      text: input,
      isBot: false,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');

    setTimeout(() => {
      const response = getBotResponse(input);
      const botMessage: Message = {
        text: response.text,
        isBot: true,
        timestamp: new Date(),
        options: response.options
      };
      setMessages(prev => [...prev, botMessage]);
    }, 500);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleOptionClick = async (option: string) => {
    setInput(option);
    const userMessage: Message = {
      text: option,
      isBot: false,
      timestamp: new Date()
    };
    setMessages(prev => [...prev, userMessage]);

    // Check if it's a specialty selection for booking
    const specialties = ['Cardiology', 'Neurology', 'Orthopedics', 'Pediatrics', 'General Medicine', 'Gynecology', 'Dermatology'];
    const selectedSpec = specialties.find(s => option.includes(s));

    if (selectedSpec) {
      setSelectedSpecialty(selectedSpec);
      setBookingStep('doctors');

      try {
        const response = await apiService.getDoctors({ specialty: selectedSpec });
        const doctors = response.data.slice(0, 5); // Show top 5 doctors

        setTimeout(() => {
          const botMessage: Message = {
            text: `Here are our top ${selectedSpec} specialists:`,
            isBot: true,
            timestamp: new Date(),
            doctors: doctors,
            options: ["üè† Main Menu", "üîÑ Change Specialty"]
          };
          setMessages(prev => [...prev, botMessage]);
        }, 500);
      } catch (error) {
        setTimeout(() => {
          const botMessage: Message = {
            text: "Sorry, I couldn't fetch doctors at the moment. Please try again or contact support.",
            isBot: true,
            timestamp: new Date(),
            options: ["üè† Main Menu", "üìû Contact Support"]
          };
          setMessages(prev => [...prev, botMessage]);
        }, 500);
      }
      return;
    }

    setTimeout(() => {
      const response = getBotResponse(option);
      const botMessage: Message = {
        text: response.text,
        isBot: true,
        timestamp: new Date(),
        options: response.options,
        showBooking: response.showBooking
      };
      setMessages(prev => [...prev, botMessage]);
    }, 500);
  };

  const handleBookDoctor = (doctorId: string, doctorName: string) => {
    const token = localStorage.getItem('token');
    if (!token) {
      const botMessage: Message = {
        text: "‚ö†Ô∏è Please sign in first to book an appointment.\n\nClick 'Sign In' at the top of the page to continue.",
        isBot: true,
        timestamp: new Date(),
        options: ["üè† Main Menu", "üìû Contact Support"]
      };
      setMessages(prev => [...prev, botMessage]);
      return;
    }

    const botMessage: Message = {
      text: `‚úÖ Great! To complete your booking with Dr. ${doctorName}:\n\n1. Close this chat\n2. Find Dr. ${doctorName} in the doctors list\n3. Click 'Book Appointment'\n4. Select your preferred date and time\n\nOr I can help you with something else!`,
      isBot: true,
      timestamp: new Date(),
      options: ["üîÑ Choose Another Doctor", "üè† Main Menu", "üìû Need Help?"]
    };
    setMessages(prev => [...prev, botMessage]);

    // Scroll to doctor in main page
    setTimeout(() => {
      setIsOpen(false);
      const doctorElement = document.querySelector(`[data-doctor-id="${doctorId}"]`);
      if (doctorElement) {
        doctorElement.scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
    }, 2000);
  };

  return (
    <>
      {/* Chat Button */}
      {!isOpen && (
        <button
          onClick={() => setIsOpen(true)}
          className="fixed bottom-6 right-6 w-16 h-16 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-full shadow-2xl hover:shadow-3xl transform hover:scale-110 transition-all duration-300 flex items-center justify-center z-50 animate-bounce"
        >
          <MessageCircle className="h-7 w-7" />
        </button>
      )}

      {/* Chat Window */}
      {isOpen && (
        <div className="fixed bottom-6 right-6 w-96 h-[600px] bg-white rounded-2xl shadow-2xl flex flex-col z-50 border border-gray-200 overflow-hidden">
          {/* Header */}
          <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-4 flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-white bg-opacity-20 rounded-full flex items-center justify-center">
                <Bot className="h-6 w-6" />
              </div>
              <div>
                <h3 className="font-bold text-lg">SehatNama Assistant</h3>
                <p className="text-xs text-blue-100">Online ‚Ä¢ Always here to help</p>
              </div>
            </div>
            <button
              onClick={() => setIsOpen(false)}
              className="hover:bg-white hover:bg-opacity-20 rounded-full p-2 transition-all"
            >
              <X className="h-5 w-5" />
            </button>
          </div>

          {/* Messages */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gray-50">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`flex ${message.isBot ? 'justify-start' : 'justify-end'}`}
              >
                <div
                  className={`max-w-[80%] rounded-2xl px-4 py-3 ${
                    message.isBot
                      ? 'bg-white text-gray-800 shadow-md border border-gray-200'
                      : 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg'
                  }`}
                >
                  {message.isBot && (
                    <div className="flex items-center space-x-2 mb-1">
                      <Bot className="h-4 w-4 text-blue-600" />
                      <span className="text-xs font-semibold text-blue-600">Assistant</span>
                    </div>
                  )}
                  <p className="text-sm whitespace-pre-line leading-relaxed">{message.text}</p>
                  <p className={`text-xs mt-1 ${message.isBot ? 'text-gray-400' : 'text-blue-100'}`}>
                    {message.timestamp.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' })}
                  </p>
                  {message.isBot && message.doctors && (
                    <div className="mt-3 space-y-3">
                      {message.doctors.map((doctor) => (
                        <div key={doctor._id} className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-lg p-3 border border-blue-200">
                          <div className="flex items-start justify-between">
                            <div className="flex-1">
                              <h4 className="font-bold text-gray-900 text-sm">Dr. {doctor.name}</h4>
                              <p className="text-xs text-gray-600">{doctor.specialty}</p>
                              <div className="flex items-center gap-2 mt-1">
                                <span className="text-xs text-yellow-600">‚≠ê {doctor.rating}</span>
                                <span className="text-xs text-gray-500">‚Ä¢</span>
                                <span className="text-xs text-gray-600">{doctor.experience}y exp</span>
                                <span className="text-xs text-gray-500">‚Ä¢</span>
                                <span className="text-xs text-green-600 font-semibold">‚Çπ{doctor.consultationFee}</span>
                              </div>
                            </div>
                            <button
                              onClick={() => handleBookDoctor(doctor._id, doctor.name)}
                              className="ml-2 bg-gradient-to-r from-blue-600 to-purple-600 text-white px-3 py-1.5 rounded-lg text-xs font-semibold hover:shadow-lg transform hover:scale-105 transition-all flex items-center gap-1"
                            >
                              <Calendar className="h-3 w-3" />
                              Book
                            </button>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                  {message.isBot && message.options && (
                    <div className="mt-3 space-y-2">
                      {message.options.map((option, idx) => (
                        <button
                          key={idx}
                          onClick={() => handleOptionClick(option)}
                          className="block w-full text-left px-3 py-2 bg-gradient-to-r from-blue-50 to-purple-50 hover:from-blue-100 hover:to-purple-100 text-gray-700 rounded-lg text-sm font-medium transition-all transform hover:scale-105 border border-blue-200"
                        >
                          {option}
                        </button>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          {/* Input */}
          <div className="p-4 bg-white border-t border-gray-200">
            <div className="flex items-center space-x-2">
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Type your message..."
                className="flex-1 px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent text-sm"
              />
              <button
                onClick={handleSend}
                disabled={!input.trim()}
                className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-3 rounded-xl hover:shadow-lg transform hover:scale-105 transition-all disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <Send className="h-5 w-5" />
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Chatbot;
