import React from 'react';
import { Doctor } from '../types';
import DoctorCard from './DoctorCard';

interface DoctorSectionsProps {
  doctors: Doctor[];
  onBookAppointment: (doctorId: string) => void;
  onViewProfile: (doctorId: string) => void;
  userLocation?: { lat: number; lng: number } | null;
}

const DoctorSections: React.FC<DoctorSectionsProps> = ({ 
  doctors, 
  onBookAppointment, 
  onViewProfile,
  userLocation 
}) => {
  const affordableDoctors = [...doctors].sort((a, b) => a.consultationFee - b.consultationFee).slice(0, 3);
  const topRatedDoctors = [...doctors].sort((a, b) => b.rating - a.rating).slice(0, 3);
  const experiencedDoctors = [...doctors].sort((a, b) => b.experience - a.experience).slice(0, 3);
  const popularDoctors = [...doctors].sort((a, b) => (b.reviewCount || 0) - (a.reviewCount || 0)).slice(0, 3);

  const Section = ({ title, icon, doctors, gradient }: { title: string; icon: string; doctors: Doctor[]; gradient: string }) => (
    <div className="mb-8">
      <div className={`flex items-center gap-3 mb-4 pb-2 border-b-2 ${gradient}`}>
        <span className="text-2xl">{icon}</span>
        <h2 className="text-xl font-bold text-gray-900">{title}</h2>
        <span className="text-sm text-gray-500">({doctors.length})</span>
      </div>
      <div className="space-y-3">
        {doctors.map((doctor, idx) => (
          <DoctorCard
            key={doctor._id}
            doctor={doctor}
            onBookAppointment={onBookAppointment}
            onViewProfile={onViewProfile}
            index={idx}
            userLocation={userLocation}
          />
        ))}
      </div>
    </div>
  );

