        gradient="border-green-500"
      />
      <Section 
        title="Top Rated" 
        icon="â­" 
        doctors={topRatedDoctors}
        gradient="border-yellow-500"
      />
      <Section 
        title="Most Experienced" 
        icon="ðŸŽ“" 
        doctors={experiencedDoctors}
        gradient="border-blue-500"
      />
      <Section 
        title="Most Popular" 
        icon="ðŸ”¥" 
        doctors={popularDoctors}
        gradient="border-purple-500"
      />
    </div>
  );
};

export default DoctorSections;
